{
    W const x = (etats[nEtat] & masqueGauche) | (etats[(nEtat+1)%n] & masqueDroit);
    W xA = x >> 1;

    if (x%2) xA ^= a;
                                        
    return etats[(nEtat+m)%n] ^ xA;
}


